mapper.update:
UPDATE PRODUCTS 
SET    STATUS = #{status},
       UPDATE_USER = #{updateUser},
       UPDATE_DATE = SYSDATE

           , NOTE = CASE 
               WHEN NOTE IS NULL OR LENGTH(TRIM(NOTE)) = 0 THEN #{note}
               ELSE NOTE || CHR(10) || #{note}
           END

WHERE  PRODUCT_ID = #{productId}
  AND  STATUS = '040002'

mapper.select:
SELECT COUNT(*)
FROM   PRODUCTS
WHERE  PRODUCT_ID = #{productId}

SELECT STATUS 
FROM   PRODUCTS 
WHERE  PRODUCT_ID = #{productId}
